  position: relative;
  height: 140px;
  overflow: hidden;
}
@media all and (min-width: 900px) {
.wrap {
    height: 240px;
}
}
.panelBox {
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  height: 100%;
}
.transitionByPanelBox {
  -webkit-transition: -webkit-transform .2s;
  transition: -webkit-transform .2s;
  transition: transform .2s;
  transition: transform .2s, -webkit-transform .2s;
}
.panel {
  height: 100%;
}
.panel-img {
  width: 100%;
  height: 100%;
}
.dotBox {
  position: absolute;
  bottom: 10px;
  left: 36%;
  right: 36%;
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  -webkit-box-pack: justify;
      -ms-flex-pack: justify;
          justify-content: space-between;
}
.dot {
  display: inline-block;
  width: 10px;
  height: 10px;
  border-radius: 50%;
  background: rgba(255, 255, 255, .6);
  cursor: pointer;
}
@media all and (min-width: 900px) {
.dot {
    width: 20px;
    height: 20px;
}
}
.dot-active {
  background: #fff;
}
